CREATE TABLE IF NOT EXISTS DEPARTMENTS (ID INT AUTO_INCREMENT PRIMARY KEY, DISPLAY_NAME VARCHAR(255) NOT NULL);
CREATE TABLE IF NOT EXISTS POSTS (ID UUID PRIMARY KEY, "date" VARCHAR(50) NOT NULL, TITLE VARCHAR(255) NOT NULL, CONTENT TEXT NOT NULL, DEPARTMENT_ID INT NOT NULL, CONSTRAINT FK_POSTS_DEPARTMENT_ID__ID FOREIGN KEY (DEPARTMENT_ID) REFERENCES DEPARTMENTS(ID) ON DELETE RESTRICT ON UPDATE RESTRICT);
